INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(635, 'Jean Hersholt Humanitarian Award', '', true, 'Sherry Lansing', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(636, 'Scientific and Technical (Scientific and Engineering Award)', '', true, 'To PHILLIP J. FEINER, JIM HOUSTON, DENIS LECONTE and CHRIS BUSHMAN of Pacific Title and Art Studio for the design and development of the Rosetta process for creating digital YCM archival masters for digital film restoration. With elements that may be recombined either digitally or optically, the Rosetta Separations process offers a uniquely great versatility in achieving high-quality results for digital YCM archiving. [Laboratory]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(637, 'Scientific and Technical (Scientific and Engineering Award)', '', true, 'To STEVE SULLIVAN, COLIN DAVIDSON, MAX CHEN and FRANCESCO CALLARI for the design and development of the ILM Image-based Modeling System. This highly integrated system facilitates interactive construction and editing of 3D models from digital photographs and addresses the three-dimensional scanning needs of motion pictures in unique and innovative ways. [Digital Imaging Technology]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(638, 'Scientific and Technical (Scientific and Engineering Award)', '', true, 'To DR. BILL COLLIS, SIMON ROBINSON, BEN KENT and DR. ANIL KOKARAM for the design and development of the Furnace integrated suite of software tools that robustly utilizes temporal coherence for enhancing visual effects in motion picture sequences. The Furnace toolset''s modularity, flexibility and robustness has set a high standard of quality for optical flow-based image manipulation. [Digital Imaging Technology]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(639, 'Scientific and Technical (Scientific and Engineering Award)', '', true, 'To HOWARD PRESTON and MIRKO KOVACEVIC for the design and engineering of the Preston Cinema Systems FI+Z wireless remote system. Pioneering unprecedented reliability and flexibility in wireless lens and camera operation, the FI+Z has continued to be a leader in innovation since its introduction in 1994. [Photography]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(640, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To JOSHUA PINES and CHRIS KUTCKA of Technicolor Digital Intermediates for the design and development of the TDI process for creating archival separations from digital image data. The TDI process is based on the production of digital separation negatives creating archival elements that can be scanned and digitally recombined in the future. [Laboratory]', 2006);
