INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(693, 'Directing', 'Steven Spielberg', false, 'Munich', 2005);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(641, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To WILLIAM FEIGHTNER and CHRIS EDWARDS of E-Film for the design and development of the E-Film process for creating archival separations from digital image data. The E-Film process is based on the production of digital separation negatives creating archival elements that can be scanned and digitally recombined in the future. [Laboratory]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(642, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To ALBERT RIDILLA, PAPKEN SHAHBAZIAN, RONALD BELKNAP and JAY McGARRIGLE for the design and development of the Hollywood Film Company Brumagic MPST Densitometer. The Brumagic MPST was designed specifically to measure density in the motion picture soundtrack and has become the densitometer of choice for reading soundtrack negative and positive densities worldwide. [Sound]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(643, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To KLEMENS KEHRER, JOSEF HANDLER, THOMAS SMIDEK and MARC SHIPMAN MUELLER for the design and development of the Arriflex 235 Camera System. Designed for handheld photography, the features of this small, lightweight MOS camera also allow it to be used as a secondary production camera. [Camera]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(644, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To FLORIAN KAINZ for the design and engineering of OpenEXR, a software package implementing 16-bit, floating point, high dynamic range image files. Widely adopted, OpenEXR is engineered to meet the requirements of the visual effects industry by providing for lossless and lossy compression of tiered and tiled images. [Digital Imaging Technology]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(645, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To WALTER TRAUNINGER and ERNST TSCHIDA for the design and engineering of the Arri WRC wireless remote lens control system. This highly modular system permits accurate and reliable wireless control with multiple hand controls of all lens functions. [Photography]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(646, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To CHRISTIAN TSCHIDA and MARTIN WAITZ of cmotion for the design and engineering of the cmotion Wireless Remote System. The graphical user interface of the cmotion System eases the difficult task of following focus, and the unique lens tag system recalls the calibration for each lens. [Photography]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(647, 'Scientific and Technical (Technical Achievement Award)', '', true, 'To PETER LITWINOWICZ and PIERRE JASMIN for the design and development of the RE:Vision Effects family of software tools for optical flow-based image manipulation. A unique user interface and relatively low cost have made these tools ubiquitous in the visual effects community. [Digital Imaging Technology]', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(648, 'Scientific and Technical (Gordon E. Sawyer Award)', '', true, 'Ray Feeney', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(649, 'Scientific and Technical (Bonner Medal)', '', true, 'To Richard Edlund in appreciation for outstanding service and dedication in upholding the high standards of the Academy of Motion Picture Arts and Sciences.', 2006);
INSERT INTO "playerInfo".films (id, category, info, won, nominee, "year") VALUES(650, 'Scientific and Technical (Special Awards)', '', true, 'To Ioan Allen, J. Wayne Anderson, Mary Ann Anderson, Ted Costas, Paul R. Goldberg, Shawn Jones, Thomas Kuhn, Dr. Alan Masson, Colin Mossman, Martin Richards, Frank Ricotta and Richard C. Sehlin for their contributions to the environmentally responsible industry conversion from silver-based to cyan dye analog soundtracks.', 2006);
